import express from "express";
import 'express-async-errors';
import cors from 'cors';
import Endpoints from './routes';
import connectToDatabase from "./database/models/connection";
import ErrorHandler from "./middlewares/ErrorHandler";

class App {
  public app: express.Application;

  public constructor() {
    this.app = express();
    this.config();
    this.database();
    this.routes();
  }

  private config() {
    this.app.use(express.json());
    this.app.use(cors());
  }

  private database() {
    connectToDatabase();
  }

  private routes() {
    this.app.use(Endpoints);
    this.app.use(ErrorHandler);
  }

  public start(PORT: string | number) {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

export default new App();

import { Router } from 'express';
import userRouter from './userRouter';
import questionsRouter from './questionsRouter';

const Endpoints = Router();

Endpoints.use('/', userRouter);
Endpoints.use('/questions', questionsRouter);

export default Endpoints;

import { Router } from 'express';
import UserController from '../domain/cases/login/controllers/userController';

const route = Router();

route.post('/sign_in', UserController.login);
route.post('/sign_up', UserController.create);
route.get('/user/:id', UserController.readOne);
route.put('/user/:id', UserController.update);
route.delete('/user/:id', UserController.delete);

export default route;

import { Request, Response } from 'express';
import UserService from '../services/userService';

class UserController {
  constructor(private service = UserService) { }

  public create = async (req: Request, res: Response) => {
    const result = await this.service.create(req.body);
    return res.status(201).json(result);
  };

  public login = async (req: Request, res: Response) => {
    const { email, password } = req.body;
    const user = await this.service.login(email, password);
    return res.status(200).json(user);
  };

  public readOne = async (req: Request, res: Response) => {
    const { id } = req.params;
    const result = await this.service.readOne(id);
    return res.status(200).json(result);
  };

  public update = async (req: Request, res: Response) => {
    const { id } = req.params;
    const result = await this.service.update(id, req.body);
    return res.status(200).json(result);
  };

  public delete = async (req: Request, res: Response) => {
    const { id } = req.params;
    await this.service.readOne(id);
    await this.service.delete(id);
    return res.status(204).end();
  };
}

export default new UserController();

import { IUser, UserSchema } from '../interfaces/IUser';
import UserModel from '../../../../database/models/User';
import Bcrypt from '../../../../helpers/Bcrypt';
import tokenGenerator from '../../../../helpers/TokenGenerator';
import { ErrorTypes } from '../../../../helpers/ErrorCatalog';

class UserService {
  constructor(private model = UserModel) { }

  public async create(obj: IUser) {
    const parsed = UserSchema.safeParse(obj);
    const { email, password } = obj;

    if (!parsed.success) throw parsed.error;

    const userExists = await this.model.readByEmail(email);
    if (userExists) throw new Error(ErrorTypes.UserExists);

    const encryptPass = await Bcrypt.hashPass(password);
    const user = await this.model.create({ ...obj, password: encryptPass });

    const userInfo = {
      id: user._id,
      email,
      role: user.role,
      fullName: `${user.firstName} ${user.lastName}`,
    };
    const { token } = tokenGenerator(userInfo);

    return ({ ...userInfo, token });
  }

  public async login(email: string, pass: string) {
    const user = await this.model.readByEmail(email);
    if (!user) throw new Error(ErrorTypes.UserNotFound);

    const checkPass = await Bcrypt.comparePass(pass, user.password);
    if (!checkPass) throw new Error(ErrorTypes.WrongPassword);

    const userInfo = {
      id: user._id,
      email,
      role: user.role,
      fullName: `${user.firstName} ${user.lastName}`,
    };
    const { token } = tokenGenerator(userInfo);

    return ({ ...userInfo, token });
  }

  public readOne(id: string) {
    return this.model.readOne(id);
  }

  public update(id: string, payload: IUser) {
    return this.model.update(id, payload);
  }

  public delete(id: string) {
    return this.model.delete(id);
  }
}

export default new UserService();

import { z } from 'zod';

export const UserSchema = z.object({
  email: z.string().email(),
  password: z.string().min(6, {
    message: 'Password must be at least 6 characters long',
  }),
  firstName: z.string().min(3, {
    message: 'FirstName must be at least 3 characters long',
  }),
  lastName: z.string().min(3, {
    message: 'LastName must be at least 3 characters long',
  }),
  role: z.string(),
}).strict();

export type IUser = z.infer<typeof UserSchema>;

import { model as mongooseCreateModel, Schema } from 'mongoose';
import { IUser } from '../../domain/cases/login/interfaces/IUser';
import MongoModel from './MongoModel';

const UserSchema = new Schema<IUser>({
  email: String,
  password: String,
  firstName: String,
  lastName: String,
  role: String,
}, {
  timestamps: true,
  versionKey: false,
});

class User extends MongoModel<IUser> {
  constructor(model = mongooseCreateModel('User', UserSchema)) {
    super(model);
  }

  public readByEmail(email: string) {
    return this.model.findOne({ email });
  }
}

export default new User();

import { Model } from 'mongoose';
import { IModel } from '../interfaces/IModel';

abstract class MongoModel<T> implements IModel<T> {
  constructor(protected model: typeof Model) { }

  public async create(obj: T) {
    return this.model.create({ ...obj });
  }

  public async read(): Promise<T[]> {
    return this.model.find();
  }

  public async readOne(_id: string) {
    return this.model.findOne({ _id });
  }

  public async update(_id: string, obj: Partial<T>) {
    return this.model.findByIdAndUpdate({ _id }, obj);
  }

  public async delete(_id: string) {
    return this.model.findByIdAndDelete({ _id });
  }
}

export default MongoModel;

import bcrypt from 'bcrypt';

class Bcrypt {
  public hashPass = (password: string) => {
    return bcrypt.hash(password, 10);
  };

  public comparePass = (password: string, hash: string) => {
    return bcrypt.compare(password, hash);
  };
}

export default new Bcrypt();

import { sign, Secret, SignOptions } from 'jsonwebtoken';

const SECRET: Secret = process.env.SECRET || 'vnetod';

interface userInfo {
  email: string
}

export default function tokenGenerator(payload: userInfo) {
  const jwtConfig: SignOptions = {
    expiresIn: '365d',
    algorithm: 'HS256',
  };
  const token = sign(payload, SECRET, jwtConfig);
  return { token };
};

export enum ErrorTypes {
  UserExists = 'UserExists',
  UserNotFound = 'UserNotFound',
  WrongPassword = 'WrongPassword',
  TokenNotFound = 'TokenNotFound',
  InvalidToken = 'InvalidToken',
}

type ErrorResponseObject = {
  message: string;
  code: number;
};

type catalog = {
  [key in ErrorTypes]: ErrorResponseObject
};

export const ErrorCatalog: catalog = {
  UserExists: {
    message: 'User Already Exists',
    code: 409,
  },
  UserNotFound: {
    message: 'User Not found',
    code: 404,
  },
  WrongPassword: {
    message: 'Wrong Password',
    code: 401,
  },
  TokenNotFound: {
    message: 'Token Not Found',
    code: 404,
  },
  InvalidToken: {
    message: 'Invalid Token',
    code: 401,
  },
};

import { Router } from 'express';
import questionController from '../domain/cases/questions/controllers/questionController';

const route = Router();


route.post('/', questionController.create);
route.get('/', questionController.filterQuestions);
route.get('/:id', questionController.readOne);
route.put('/:id', questionController.update);
route.delete('/:id', questionController.delete);

export default route;

import { Request, Response } from 'express';
import { IReqQuery } from '../interfaces/IReqQuery';
import questionService from '../services/questionService';

class QuestionController {
  constructor(private service = questionService) { }

  public create = async (req: Request, res: Response) => {
    const result = await this.service.create(req.body);
    return res.status(201).json(result);
  };

  public filterQuestions = async (req: Request, res: Response) => {
    const { page, limit, status, userId } = req.query as unknown as IReqQuery;
    const result = await this.service
      .filterQuestions(page, limit, status, userId);
    return res.status(200).json(result);
  };

  public readOne = async (req: Request, res: Response) => {
    const { id } = req.params;
    const result = await this.service.readOne(id);
    return res.status(200).json(result);
  };

  public update = async (req: Request, res: Response) => {
    const { id } = req.params;
    const result = await this.service.update(id, req.body);
    return res.status(200).json(result);
  };

  public delete = async (req: Request, res: Response) => {
    const { id } = req.params;
    await this.service.readOne(id);
    await this.service.delete(id);
    return res.status(204).end();
  };
}

export default new QuestionController();

import { IQuestion, QuestionSchema } from '../interfaces/IQuestion';
import QuestionModel from '../../../../database/models/Question';
import { IQuestionCount, IQuestionInfo } from '../interfaces/IQuestionInfo';

class QuestionService {
  constructor(private model = QuestionModel) { }

  public async create(obj: IQuestion): Promise<IQuestionInfo> {
    const parsed = QuestionSchema.safeParse(obj);

    if (!parsed.success) throw parsed.error;

    return await this.model.create(obj);
  }

  public filterQuestions(
    page: number,
    limit: number,
    status: string,
    userId: string,
  ): Promise<IQuestionCount[]> {
    return this.model.filterQuestions(page, limit, status, userId);
  }

  public readOne(id: string): Promise<IQuestionInfo> {
    return this.model.readOne(id);
  }

  public update(id: string, payload: IQuestion): Promise<IQuestionInfo> {
    return this.model.update(id, payload);
  }

  public delete(id: string): Promise<void> {
    return this.model.delete(id);
  }
}

export default new QuestionService();

import { z } from 'zod';

export const QuestionSchema = z.object({
  userId: z.string(),
  author: z.string(),
  question: z.string(),
  answer: z.string().url(),
  status: z.string(),
}).strict();

export type IQuestion = z.infer<typeof QuestionSchema>;

import { model as mongooseCreateModel, Schema } from 'mongoose';
import { IQuestion } from '../../domain/cases/questions/interfaces/IQuestion';
import { IQuestionCount } from '../../domain/cases/questions/interfaces/IQuestionInfo';
import MongoModel from './MongoModel';

const QuestionSchema = new Schema<IQuestion>({
  userId: String,
  author: String,
  question: String,
  answer: String,
  status: String,
}, {
  timestamps: true,
  versionKey: false,
});

class Question extends MongoModel<IQuestion> {
  constructor(model = mongooseCreateModel('Question', QuestionSchema)) {
    super(model);
  }

  public async filterQuestions(
    page: number,
    limit: number,
    status: string,
    userId: string,
  ): Promise<IQuestionCount[]> {
    const query = userId ? { userId, status } : { status };
    const questions = await this.model.aggregate()
      .match({ ...query })
      .facet({
        questions: [{
          $skip: (page - 1) * limit,
        }, {
          $limit: limit * 1,
        }, {
          $sort: { createdAt: -1 },
        }],
        total: [{ $count: 'count' }],
      });
    return questions[0];
  }
}

export default new Question();

import mongoose from 'mongoose';
import 'dotenv/config';

const MONGO_DB_URL = 'mongodb://localhost:27017/tsnode';

const connectToDatabase = (
  mongoDatabaseURI = process.env.MONGO_URI
    || MONGO_DB_URL,
) => mongoose.connect(mongoDatabaseURI);

export default connectToDatabase;

import { ErrorRequestHandler, NextFunction, Request, Response } from 'express';
import { ErrorCatalog, ErrorTypes } from '../helpers/ErrorCatalog';
import { ZodError } from 'zod';

const ErrorHandler: ErrorRequestHandler = (
  err: Error | ZodError,
  _req: Request,
  res: Response,
  _next: NextFunction,
) => {
  if (err instanceof ZodError) {
    const response = err.issues.map((z) => z.message);
    return res.status(400).json({ message: response });
  }
  const messageAsErrorType = err.message as keyof typeof ErrorTypes;
  const mappedError = ErrorCatalog[messageAsErrorType];

  if (mappedError) {
    const { code, message } = mappedError;
    return res.status(code).json({ message });
  }

  return res.status(500).json({ message: 'Internal Error' });
};

export default ErrorHandler;
